using System;using System.Collections;using System.Collections.Generic;public class SoundConfigBase{	public readonly int ID;	public readonly string Desc;	public readonly string Sound;	public readonly float Delay;	public readonly float Volume;}public class SoundConfigBaseManager: Singleton<SoundConfigBaseManager>{	private Dictionary<string, SoundConfigBase> m_dataList = new Dictionary<string, SoundConfigBase>();	private readonly long version=131599182026585800;	public int Size	{		get { return m_dataList.Count; }	}	public SoundConfigBase  Get(int index)	{		if (index > -1 && index < m_dataList.Count)		{			int i = 0;			 foreach (var tmp in m_dataList.Values)			 {				 if (index == i)				 {					return tmp;				 }				 i++;			 }		}		  return null;	}	public SoundConfigBase Find(int key1,int key2=-1,int key3=-1)	{		 string key = key1.ToString();		 if (key2 != -1) { key += key2.ToString(); }		 if (key3 != -1) { key += key3.ToString(); }		 if (m_dataList.ContainsKey(key))		 {			return m_dataList[key];		 }		 return null;	}	public SoundConfigBase Find(string key1, string key2 ="", string key3 = "")	{		 string key = key1.ToString();		 if (key2 != "") { key += key2.ToString(); }		 if (key3 != "") { key += key3.ToString(); }		 if (m_dataList.ContainsKey(key))		 {			return m_dataList[key];		 }		 return null;	}	 public bool Load(string path)	 {		 return TableUtility.instance.Load<SoundConfigBase>(path,ref m_dataList,version.ToString());	}  }
